#!/bin/bash
whois $1 | awk '/Registrant Name:|Registrant Organization:|Registrant Street:|Registrant City:|Registrant State\/Province:|Registrant Postal Code:|Registrant Country:|Registrant Phone:|Registrant Phone Ext:|Registrant Fax:|Registrant Fax Ext:|Registrant Email:|Admin Name:|Admin Organization:|Admin Street:|Admin City:|Admin State\/Province:|Admin Postal Code:|Admin Country:|Admin Phone:|Admin Phone Ext:|Admin Fax:|Admin Fax Ext:|Admin Email:|Tech Name:|Tech Organization:|Tech Street:|Tech City:|Tech State\/Province:|Tech Postal Code:|Tech Country:|Tech Phone:|Tech Phone Ext:|Tech Fax:|Tech Fax Ext:|Tech Email:/ {gsub(/: */, ","); print}' > $1.csv